mapper.select:
select di.*,ca.name as categoryName from dish di left join category ca on di.category_id = ca.id

    and di.name like contant(%,#{name},%) 
    and di.categoryId = #{categoryId}
    and di.status = #{status}

order by create_time desc

select * from dish

    and name like contant(%,#{name},%) 
    and category_id = #{categoryId}
    and status = #{status}
    and id = #{id}
    and price = #{price}
    and image = #{image}
    and description like contant(%,#{description},%) 
    and create_time = #{createTime}
    and create_user = #{createUser}
    and update_time = #{updateTime}
    and update_user = #{createUser}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, status = #{status}, id = #{id}, price = #{price}, image = #{image}, description = #{description},
where id = #{id}
